vim.keymap.set('n', '<C-b>', ':NvimTreeFocus<CR>', { noremap = true, silent = true })       -- Ctrl + B → Foca no NvimTree
vim.keymap.set('n', '<C-S-b>', ':NvimTreeToggle<CR>', { noremap = true, silent = true })     -- Ctrl + Shift + B → Abre/fecha
vim.keymap.set('n', '<C-t>', ':NvimTreeToggle<CR>', { noremap = true, silent = true }) 